import { useNavigate, Route, Routes} from "react-router-dom";
import React, { useState, useEffect } from 'react';
import './modiary.css'; 
import axios from "axios"; 

    const Modal = ({ isOpen, onClose, onSubmit }) => {
        const [text, setText] = useState(''); //textÍ∞Ä Í∞í Ï∂îÏ†ÅÌïòÍ≥† setTextÌï®Ïàò ÌÜµÌï¥ ÏÉÅÌÉú
        const [message, setMessage] = useState('');
        const [length, setLength] = useState(0);
        const [placeholder, setPlaceholder] = useState('');
        const [title, setTitle] = useState('Ïò§ÎäòÏùò Í∑∏Î¶∞ÏùºÍ∏∞');
        const [inputCount, setInputCount] = useState(0);
        const [diary_content, setDiaryContent] = useState('');

    const proverbs = [
        "ÏùºÌöåÏö©Ìíà", "Ïû¨ÌôúÏö©", "Î¨º", "Ï†ÑÍ∏∞", "Î∞∞Îã¨", "Í±∑Í∏∞", "Ïπ¥Ìéò-ÌÖÄÎ∏îÎü¨", "Î∂ÑÎ¶¨ÏàòÍ±∞", "Ï†úÎ°úÏõ®Ïù¥Ïä§Ìä∏ÏáºÌïë", "ÏπúÌôòÍ≤Ω", 
        "Î©∏Ï¢ÖÏúÑÍ∏∞", "ÏóêÏΩîÎ∞±", "ÏÑ§Í±∞ÏßÄ", "ÏûêÏ†ÑÍ±∞", "ÎåÄÏ§ëÍµêÌÜµ", "ÎØ∏ÏÑ∏Î®ºÏßÄ", "ÏãùÎ¨ºÌÇ§Ïö∞Í∏∞", "ÏÜêÎπ®Îûò", 
        "ÌîåÎ°úÍπÖ", "Î¥âÏÇ¨ÌôúÎèô", "Ìè¨Ïû•", "ÌóåÏò∑ÏàòÍ±∞"
        ];

    const clearTextarea = () => {
        setDiaryContent(''); //Í∏ÄÏùÑ ÏßÄÏõåÏ£ºÎäî Í∏∞Îä•
    }

    const getRandomIndex = (length) => {
        return Math.floor(Math.random() * length)
    };

    useEffect(() => {
        if (isOpen) {
        setPlaceholder(proverbs[getRandomIndex(proverbs.length)]);
        setTitle(proverbs[getRandomIndex(proverbs.length)]);
        }
    }, [isOpen]); 

    //Ïó¨Í∏∞Î∂ÄÌÑ∞ ÏàòÏ†ï---------------------------------------------------------------
    let onChange = (event) => {
        const value = event.target.value;
        setDiaryContent(event.target.value);
        setLength(event.target.value.length);
      };

    
    const handleSubmit = async (event) => { //ÏûÖÎ†• ÌïÑÎìúÏóêÏÑú ÏûÖÎ†•Ïù¥ Î≥ÄÍ≤ΩÎê† ÎïåÎßàÎã§ Ìò∏Ï∂úÎêòÎ©∞, ÏûÖÎ†•Îêú Í∞íÏùÑ setTextÎ•º ÌÜµÌï¥ ÏÉÅÌÉú textÏóê Ï†ÄÏû•
        event.preventDefault(); // Í∏∞Î≥∏ Ìèº Ï†úÏ∂ú ÎèôÏûë Î∞©ÏßÄ      

        console.log("ÌÖçÏä§Ìä∏ÎÇ¥Ïö©:", diary_content);
        
        try {
            const jwtToken = localStorage.getItem('userInfo') ? JSON.parse(localStorage.getItem('userInfo')).jwtToken : null;
            if (!jwtToken) { // JWT ÌÜ†ÌÅ∞Ïù¥ ÏóÜÏúºÎ©¥ Ï≤òÎ¶¨
                console.error("JWT ÌÜ†ÌÅ∞Ïù¥ ÏóÜÏäµÎãàÎã§.");
                return;
            }
    
            const response = await fetch('http://localhost:8080/post/write_diary', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${jwtToken}`
                },
                body: JSON.stringify({ diary_content }), // content ÏÉÅÌÉú Í∞íÏùÑ JSONÏúºÎ°ú Î≥ÄÌôòÌïòÏó¨ ÏöîÏ≤≠ Î≥∏Î¨∏Ïóê Ìè¨Ìï®
            });
    
            if (!response.ok) {
                // ÏöîÏ≤≠Ïù¥ ÏÑ±Í≥µÌïòÏßÄ ÏïäÏïòÏùÑ Îïå Ï≤òÎ¶¨
                console.error("ÏöîÏ≤≠ Ïã§Ìå®:", response.statusText);
                return;
            }

            const data = await response.json(); // ÏùëÎãµ Îç∞Ïù¥ÌÑ∞Î•º Î∞õÏïÑÏôÄÏÑú data Î≥ÄÏàòÏóê Ìï†Îãπ
            setMessage('Ï†ÄÏû•ÎêêÏäµÎãàÎã§.');
            // onSubmit(text); // Í∏Ä Îì±Î°ù Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú ÎèôÏûë
            alert("Ï†ÄÏû•ÎêêÏäµÎãàÎã§."); // ÏöîÏ≤≠ ÏÑ±Í≥µ Ïãú Ï≤òÎ¶¨
            console.log("ÏùëÎãµ Îç∞Ïù¥ÌÑ∞:", data);

            //Ìèº Ï¥àÍ∏∞Ìôî
            setDiaryContent(''); // ÌÖçÏä§Ìä∏ ÏûÖÎ†•Ïπ∏ÏùÑ Ï¥àÍ∏∞Ìôî=> Îã§Ïùå ÏûÖÎ†•ÏúÑÌï¥ ÎπàÌïÑÎìúÍ∞ñÍ≤åÌï®
            setLength(0);
            onClose(); //Ï†ÄÏû•ÌïòÎ©¥ ÌïòÎ°ú Îã´Í∏∞Í≤åÌï® ÏßÄÏ≤¥„Ñ¥„Ñ¥
            
        } catch (error) {
            console.error("API ÏöîÏ≤≠ Ïò§Î•ò:", error);
    }
}

    if (!isOpen) {
        return null; // Î™®Îã¨Ïù¥ Îã´ÌòÄ ÏûàÏùÑ ÎïåÎäî Î†åÎçîÎßÅÌïòÏßÄ ÏïäÏùå
    }

    async function greenDiary() {
        const REACT_APP_API = 'http://localhost:8080/post/write_diary';
        try {
            const urlParams = new URLSearchParams(window.location.search);
            const code = urlParams.get('code');
            const state = urlParams.get('state');
        
            const res = await axios.post(REACT_APP_API + `/api/user-info`, { code, state });
            const { accessToken, refreshToken, diary_id, diary_content, login_id, jwtToken } = res.data;
        
            const userInfo = {
              diary_id,
              diary_content,
              login_id,
              accessToken,
              refreshToken,
              jwtToken // jwtToken Ï∂îÍ∞Ä
            };

            localStorage.setItem('userInfo', JSON.stringify(userInfo));
    
            // Î¶¨ÎîîÎ†âÏÖò ÏóÜÏù¥ ÏïåÎ¶ºÎßå ÌëúÏãú
            alert("Ï†ÄÏû•ÎêêÏäµÎãàÎã§.");
        
            // Î¶¨ÎîîÎ†âÏÖòÏùÑ ÏúÑÌï¥ ÌïÑÏöîÌïú ÏΩîÎìú
            window.location.href = "/home";
        } catch (error) {
            console.error("Ïò§Î•ò:", error);
        }
    }

    return (
            <div className="modal">
                    <button className="Close-button" onClick={() => {onClose(); clearTextarea();}} type="button">
                        close
                    </button>
                    <h3>üåû Ïò§ÎäòÏùò Ï£ºÏ†ú  {'<'}{title}{'>'} üåû</h3>
                    <div style={{ display: isOpen ? 'block' : 'none' }}> //isOpenÏù¥'block'Ïù¥Î©¥ ÏöîÏÜåÍ∞Ä Î≥¥Ïù¥Í≥†, 'none'Ïù¥Î©¥ ÏöîÏÜåÍ∞Ä Î≥¥Ïù¥ÏßÄ ÏïäÏùå
                    <form onSubmit={handleSubmit}>
                        <textarea 
                            className ="diarytextarea"
                            maxLength={199}
                            // maxLength="200" alert="200ÏûêÍ∞Ä Îã§ Ï±ÑÏõåÏ°åÏäµÎãàÎã§."
                            value={diary_content} //ÏûÖÎ†•Îêú Í∞íÏùÑ textÏôÄ Ïó∞Í≤∞
                            onChange={onChange} // ÏûÖÎ†•Îêú ÌÖçÏä§Ìä∏ Î≥ÄÍ≤Ω Ïãú ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
                            placeholder="Ïò§ÎäòÏùò ÏùºÍ∏∞Î•º ÏûëÏÑ±ÌïòÏÑ∏Ïöî. ÏùºÍ∏∞ ÏûëÏÑ± ÌõÑ ÏÇ¨Í≥ºÎ•º ÎàåÎü¨ ÏùºÍ∏∞Î•º Ï†ÄÏû•Ìï¥Ï£ºÏÑ∏Ïöî!"
                            style={{ whiteSpace: "pre-wrap" }}
                        ></textarea>
                        <div className="diaryinputLength">{length}/200Ïûê</div>
                        <button className="Save-button" type="submit">
                            <img src='apple.png' alt="Save" />
                        </button>
                        </form>
                        </div>
                        {message && <p>{message}</p>}
             </div>
    );
};


export default Modal;
