import React, { useEffect, useState } from "react";
import { Routes, Route, Link, useNavigate } from "react-router-dom";
import axios from "axios";
import Modal from "./modiary.js";
import Home from "./Home.js";
import History from "./history.js";
import Pagination from "react-js-pagination";
import "./App.css";

function Notice() {
  const [userInformation, setUserInformation] = useState({});
  const [text, setText] = useState("");
  const [length, setLength] = useState(0);
  const [oldText, setOldText] = useState("");
  const [isModalOpen, setModalOpen] = useState(false);
  const [userWriteInformation, setUserWriteInformation] = useState([]);
  const [email, setEmail] = useState("");
  const [jwtToken, setJwtToken] = useState("");
  const [page, setPage] = useState(1);
  const [items, setItems] = useState(2);
  const [editId, setEditId] = useState(null);

  const navigate = useNavigate();

  useEffect(() => {
    getBoardList();
  }, []);

  useEffect(() => {
    const userInfoFromStorage = localStorage.getItem("userInfo");
    if (userInfoFromStorage) {
      const userInfo = JSON.parse(userInfoFromStorage);
      setUserInformation(userInfo);
      setEmail(userInfo.email);
      setJwtToken(userInfo.jwtToken);
    }
  }, []);

  const getBoardList = async () => {
    try {
      const response = await axios.get("http://localhost:8080/api/board/list?page=0&size=7");
      setUserWriteInformation(response.data.content);
    } catch (error) {
      console.error("게시글 목록을 가져오는 중 오류 발생:", error);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const jwtToken = localStorage.getItem("userInfo")
        ? JSON.parse(localStorage.getItem("userInfo")).jwtToken
        : null;
      if (!jwtToken) {
        console.error("JWT 토큰이 없습니다.");
        return;
      }

      const response = await fetch("http://localhost:8080/api/board/write", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${jwtToken}`,
        },
        body: JSON.stringify({ text }),
      });

      if (!response.ok) {
        console.error("요청 실패:", response.statusText);
        return;
      }

      alert("등록되었습니다.");
      setText("");
      setLength(0);
      getBoardList();
    } catch (error) {
      console.error("API 요청 오류:", error);
    }
  };

  const sendUpdateToServer = async (id, data) => {
    try {
      const jwtToken = localStorage.getItem("userInfo")
        ? JSON.parse(localStorage.getItem("userInfo")).jwtToken
        : null;
      if (!jwtToken) {
        console.error("JWT 토큰이 없습니다.");
        return;
      }

      const response = await axios.put(`http://localhost:8080/api/board/update/${id}`, data, {
        headers: { Authorization: `Bearer ${jwtToken}` },
      });
      console.log("수정 성공:", response.data);
      setUserWriteInformation(
        userWriteInformation.map((item) =>
          item.id === id ? { ...item, ...data } : item
        )
      );
    } catch (error) {
      console.error("수정 실패:", error);
    }
  };

  const sendDeleteToServer = async (id) => {
    try {
      const jwtToken = localStorage.getItem("userInfo")
        ? JSON.parse(localStorage.getItem("userInfo")).jwtToken
        : null;
      if (!jwtToken) {
        console.error("JWT 토큰이 없습니다.");
        return;
      }

      const response = await axios.delete(`http://localhost:8080/api/board/delete/${id}`, {
        headers: { Authorization: `Bearer ${jwtToken}` },
      });
      console.log("삭제 성공:", response.data);
      setUserWriteInformation(userWriteInformation.filter((item) => item.id !== id));
    } catch (error) {
      console.error("삭제 실패:", error);
    }
  };

  const loadDataToTextarea = (id, content) => {
    setText(content);
    setLength(content.length);
    setEditId(id);
  };

  const onChange = (event) => {
    const value = event.target.value;
    setText(value);
    setLength(value.length);
  };

  const handlePageChange = (page) => {
    setPage(page);
  };

  const itemChange = (e) => {
    setItems(Number(e.target.value));
  };

  const openModal = (event) => {
    event.preventDefault();
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  return (
    <div>
      <div>
        <h5>
          {userInformation.name}님,
          <br />
          환영합니다.
          <br />
          <br />
          <div className="one"></div>
        </h5>
        <ul className="navigation-menu">
          <li>
            <Link to="/Home">홈</Link>
          </li>
          <br />
          <li>
            <div className="click">게시판</div>
          </li>
          <br />
          <li>
            <Link to="/History">히스토리</Link>
          </li>
          <br />
        </ul>
        <Routes>
          <Route path="/Home" element={<Home />}></Route>
          <Route path="/History" element={<History />}></Route>
        </Routes>
        <Modal isOpen={isModalOpen} onClose={closeModal} />
      </div>
      <div className="input_data_list">
        <textarea
          className="input"
          placeholder="내용을 입력하세요"
          style={{ whiteSpace: "pre-wrap" }}
          onChange={onChange}
          value={text}
          maxLength={199}
        ></textarea>
        <div className="inputLength">{length}/200자</div>
        <div>
          <button
            className="backrock_button"
            style={{ whiteSpace: "pre-wrap" }}
            onClick={(e) => {
              e.preventDefault();
              setOldText(text);
              alert(editId ? "수정되었습니다." : "등록되었습니다.");
              if (editId) {
                sendUpdateToServer(editId, { content: text });
                setEditId(null);
              } else {
                handleSubmit(e); // handleSubmit에 이벤트 객체를 전달합니다.
              }
              setText("");
              setLength(0);
            }}
          >
            <img src="backrock_button.png" alt="backrock button" />
          </button>
        </div>

        {userWriteInformation.map((a, i) => {
          const canModifyAndDelete = email === a.email;
          return (
            <div key={i}>
              <div className="line1" />
              <div className="userdata">
                <div className="bar">
                  <div className="title">{a.title}</div>
                  <div className="writetime">Price: {a.price}</div>
                </div>
                {canModifyAndDelete && (
                  <div>
                    <button
                      className="delete"
                      onClick={() => sendDeleteToServer(a.id)}
                    >
                      <img src="deleteButton.png" alt="delete button" />
                    </button>
                    <button
                      className="modify"
                      onClick={() => loadDataToTextarea(a.id, a.content)}
                    >
                      <img src="modifyButton.png" alt="modify button" />
                    </button>
                  </div>
                )}
                <div className="noticeContent">{a.content}</div>
                <br />
                <br />
                <br />
              </div>
            </div>
          );
        })}
        <Pagination
          className="pagination"
          activePage={page}
          itemsCountPerPage={items}
          totalItemsCount={userWriteInformation.length}
          pageRangeDisplayed={5}
          onChange={handlePageChange}
        ></Pagination>
      </div>
    </div>
  );
}

export default Notice;
