        "Î©∏Ï¢ÖÏúÑÍ∏∞", "ÏóêÏΩîÎ∞±", "ÏÑ§Í±∞ÏßÄ", "ÏûêÏ†ÑÍ±∞", "ÎåÄÏ§ëÍµêÌÜµ", "ÎØ∏ÏÑ∏Î®ºÏßÄ", "ÏãùÎ¨ºÌÇ§Ïö∞Í∏∞", "ÏÜêÎπ®Îûò", 
        "ÌîåÎ°úÍπÖ", "Î¥âÏÇ¨ÌôúÎèô", "Ìè¨Ïû•", "ÌóåÏò∑ÏàòÍ±∞"
        ];

    let onChange = (event) => {
            const value = event.target.value;
            setText(value);
            setLength(value.length);
          };

    // let onChange = (event) => { 
    //     setText(event.target.value); 
    //     setText(value);
    //     setLength(value.length);
    // }; 

    const clearTextarea = () => {
        setText(''); //Í∏ÄÏùÑ ÏßÄÏõåÏ£ºÎäî Í∏∞Îä•
    }

    const getRandomIndex = (length) => {
        return Math.floor(Math.random() * length)
    };

    const MAX_LENGTH = 200;

    useEffect(() => {
        if (isOpen) {
        setPlaceholder(proverbs[getRandomIndex(proverbs.length)]);
        setTitle(proverbs[getRandomIndex(proverbs.length)]);
        }
    }, [isOpen]);

    //------------------------------------------------------------------------------
    // function modalToken({ setGetToken, setUserInfo }) {

    //     const userAccessToken = () => {
    //         const url = new URL(window.location.href);
    //         const token = url.searchParams.get("access_token");
    //         if (token) {
    //         getToken(token);
    //         }
    //     };
        
    //     const getToken = (token) => {
    //         console.log(token);
    //         localStorage.setItem("access_token", token);
    //         setGetToken(token);
    //     };
    // };
    //Ïó¨Í∏∞Î∂ÄÌÑ∞ ÏàòÏ†ï---------------------------------------------------------------

    const handleSubmit = async (event) => { //ÏûÖÎ†• ÌïÑÎìúÏóêÏÑú ÏûÖÎ†•Ïù¥ Î≥ÄÍ≤ΩÎê† ÎïåÎßàÎã§ Ìò∏Ï∂úÎêòÎ©∞, ÏûÖÎ†•Îêú Í∞íÏùÑ setTextÎ•º ÌÜµÌï¥ ÏÉÅÌÉú textÏóê Ï†ÄÏû•

        onSubmit(text); // Í∏Ä Îì±Î°ù Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú ÎèôÏûë
        setText(''); // ÌÖçÏä§Ìä∏ ÏûÖÎ†•Ïπ∏ÏùÑ Ï¥àÍ∏∞Ìôî=> Îã§Ïùå ÏûÖÎ†•ÏúÑÌï¥ ÎπàÌïÑÎìúÍ∞ñÍ≤åÌï®
