    //         window.location.href = "/modiary";
    //     } catch (error) {
    //         console.error("Ïò§Î•ò:", error);
    //     }
    // }
    //---------------------------------------------------------------------------
    if (!isOpen) {
        return null; // Î™®Îã¨Ïù¥ Îã´ÌòÄ ÏûàÏùÑ ÎïåÎäî Î†åÎçîÎßÅÌïòÏßÄ ÏïäÏùå
    }
    
    return (
            <div className="modal">
                    <button className="Close-button" onClick={() => {onClose(); clearTextarea();}}>
                        close
                    </button>
                    <h3>üåû Ïò§ÎäòÏùò Ï£ºÏ†ú  {'<'}{title}{'>'} üåû</h3>
                    <div style={{ display: isOpen ? 'block' : 'none' }}> //isOpenÏù¥'block'Ïù¥Î©¥ ÏöîÏÜåÍ∞Ä Î≥¥Ïù¥Í≥†, 'none'Ïù¥Î©¥ ÏöîÏÜåÍ∞Ä Î≥¥Ïù¥ÏßÄ ÏïäÏùå
                    <form onSubmit={handleSubmit}>
                        <textarea 
                            className ="diarytextarea"
                            maxLength={199}
                            // maxLength="200" alert="200ÏûêÍ∞Ä Îã§ Ï±ÑÏõåÏ°åÏäµÎãàÎã§."
                            value={text} //ÏûÖÎ†•Îêú Í∞íÏùÑ textÏôÄ Ïó∞Í≤∞
                            onChange={onChange} // ÏûÖÎ†•Îêú ÌÖçÏä§Ìä∏ Î≥ÄÍ≤Ω Ïãú ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
                            placeholder="Ïò§ÎäòÏùò ÏùºÍ∏∞Î•º ÏûëÏÑ±ÌïòÏÑ∏Ïöî. ÏùºÍ∏∞ ÏûëÏÑ± ÌõÑ ÏÇ¨Í≥ºÎ•º ÎàåÎü¨ ÏùºÍ∏∞Î•º Ï†ÄÏû•Ìï¥Ï£ºÏÑ∏Ïöî!"
                            style={{ whiteSpace: "pre-wrap" }}
