import logo from './logo.svg';
import './App.css';
import {Routes, Route,Link, useNavigate} from "react-router-dom";
import Modal from './modiary';
import Home from "./Home.js";
import Notice from "./Notice.js";
import History7day from "./History7day.js";
import { useState } from "react";

function History() {
  const [buttonOpen, setButtonOpen] = useState(false);
  const [isModalOpen, setModalOpen] = useState(false);//useState사용하여 상태 초기화 및 모달의 열림/닫힘 상태관리
  
  //모달열기
  const openModal = (event) => {
    event.preventDefault(); // 링크의 기본 동작 방지
    setModalOpen(true); //setModalOpen(true)를 호출하여 isModalOpen 상태를 true로 설정해 모달 열기
  };

  //모달닫기함수
  const closeModal = () => {
    setModalOpen(false); // 모달 닫기
  };

  //   useEffect(() => {
  //   fetchData();
  // }, []); 

  // async function fetchData() {
  //   try {
  //     const response = await fetch('https://api.example.com/visitor');
  //     const data = await response.json();
  //     setVisitorData(data); // 서버에서 받아온 데이터를 상태에 저장
  //   } catch (error) {
  //     console.error('데이터를 가져오는 중 오류 발생:', error);
  //   }
  // }
  const applebox = [
    { id: 1, src: 'applebox.png', style: { top: '31%', left: '56%' } },
    { id: 2, src: 'applebox.png', style: { top: '38%', left: '63%' } },
    { id: 3, src: 'applebox.png', style: { top: '38%', left: '49%' } },
    { id: 4, src: 'applebox.png', style: { top: '44%', left: '59%' } },
    { id: 5, src: 'applebox.png', style: { top: '51%', left: '65%' } },
    { id: 6, src: 'applebox.png', style: { top: '53%', left: '48%' } },
  ];

  return (
    <>
    <div className="App">
      <div>
        <h5>
           방문자님,<br />
          환영합니다.<br /><br />
          <div className="one"></div>
        </h5>
        
        <ul className="navigation-menu">
          <li><Link to="/Home">홈</Link></li><br />
          <li><Link to="/Notice">게시판</Link></li><br />
          <li><div className="click">히스토리</div></li><br />
        </ul>
        
        <Routes>
          <Route path="/Home" element={<Home />}></Route>
          <Route path="/Notice" element={<Notice />}></Route>
          <Route path="/History7day" element={<History7day />}></Route>
        </Routes>
      </div>
    <div className="Applebox">
      {applebox.map(applebox => (
        <button 
        key={applebox.id}
        className="applebox"
        onClick={openModal}
        style={{position: 'absolute', ...applebox.style}}
        >
          <img 
          src={applebox.src}
          alt={'Apple ${applebox.id}'}
          />
        </button>
      ))}
    <Modal isOpen={isModalOpen} onClose={closeModal} />

    </div>

      </div>
    </>
  );
}



export default History;
